<?phpclass Model {    function __construct() {        $this->db = new Database();    }    public function login() {        $sth = $this->db->prepare("SELECT id FROM users WHERE            start = :start AND password = MD5(:password)");        $sth->execute(array(            ':start' => $_POST['start'],            ':password' => $_POST['password']        ));        $count = $sth->rowCount();        if ($count > 0) {            // start            Session::init();            Session::set('loggedIn', true);            // header('location: ../');        } else {            // show an error!            header('location: ../start');        }    }}